components/common/paginator:
{{#and
    (if show_dropdown)
    (if (length links) '>' 1)
}}
    {{#or (if page_type 'category') (if page_type 'search')}}
        {{> components/custom/paginator-dropdown page_count=paginator_dropdown_page_count}}
    {{else}}
        {{> components/custom/paginator-dropdown page_count=(get 'number' (last links))}}
    {{/or}}
{{else}}
    {{#if (length links) '>' 1}}
        <nav class="pagination" aria-label="pagination">
            <ul class="pagination-list">
                {{#if previous}}
                    <li class="pagination-item pagination-item--previous">
                        <a class="pagination-link"
                        href="{{previous}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.previous'}}"
                        >
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-left"></use>
                                </svg>
                            </i>
                            {{lang 'common.previous'}}
                        </a>
                    </li>
                {{/if}}

                {{#each links}}
                    <li class="pagination-item {{#if this.number '==' ../current}}pagination-item--current{{/if}}">
                        <a class="pagination-link"
                        href="{{this.url}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        {{#if this.number '==' ../current}}
                        aria-current="page"
                        data-pagination-current-page-link
                        {{/if}}
                        aria-label="{{lang 'common.paginator.page_of' current=this.number total=../links.length}}"
                        >
                            {{this.number}}
                        </a>
                    </li>
                {{/each}}

                {{#if next}}
                    <li class="pagination-item pagination-item--next">
                        <a class="pagination-link"
                        href="{{next}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.next'}}"
                        >
                            {{lang 'common.next'}}
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-right"></use>
                                </svg>
                            </i>
                        </a>
                    </li>
                {{/if}}
            </ul>
        </nav>
    {{/if}}
{{/and}}

components/products/reviews:
{{#if reviews.total}}
<h2 class="page-heading page-heading--reviews">Reviews</h2>
<section class="toggle productReviews" id="product-reviews" data-product-reviews>
    <div class="title-button-wrapper">
        <h4 class="toggle-title h2">
            {{lang 'products.reviews.header' total=reviews.total}}
            {{#if theme_settings.show_product_reviews}}
            {{!-- 
                <a class="toggleLink is-open" href="#productReviews-content">
                    <span class="toggleLink-text toggleLink-text--on">
                        {{lang 'products.reviews.hide'}}
                    </span>
                    <span class="toggleLink-text toggleLink-text--off">
                        {{lang 'products.reviews.show'}}
                    </span>
                </a>
                --}}
            {{/if}}
        </h4>
        {{#if settings.show_product_reviews}}
            <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                class="productView-reviewLink productView-reviewLink--new button button--secondary"
                {{#unless is_ajax}}data-reveal-id="modal-review-form"{{/unless}}
                role="button"
            >
                {{lang 'products.reviews.new'}}
            </a>
            {{> components/products/modals/writeReview}}
        {{/if}}
    </div>
    <div class="toggle-content is-open" id="productReviews-content" aria-hidden="false">
        <ul class="productReviews-list" id="productReviews-list">
            {{#each reviews.list}}
            <li class="productReview">
                <article itemprop="review" itemscope itemtype="https://schema.org/Review">
                    <header>
                        <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="https://schema.org/Rating">
                            {{> components/products/ratings rating=rating}}
                            <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                        </span>
                        <h5 itemprop="name" class="productReview-title h3">{{{ sanitize title }}}</h5>
                        {{#if name}}
                            <meta itemprop="author" content="{{name}}">
                            <p class="productReview-author h6">
                                {{{lang 'products.reviews.post_on_by' date=date name=(sanitize name) }}}
                            </p>
                        {{else}}
                            <p class="productReview-author h6">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p itemprop="reviewBody" class="productReview-body">{{{ sanitize text }}}</p>
                </article>
            </li>
            {{/each}}
        </ul>
        {{> components/common/paginator pagination.reviews reload=true}}
    </div>
</section>
{{/if}}
